import React, { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import ApiService from '../../service/ApiService';

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await ApiService.getAllBookings();
        const bookings = response.bookingList; // ‚úÖ Fix this

        console.log("üì¶ Bookings fetched:", bookings);

        const calendarEvents = [];

        bookings.forEach((booking) => {
          const date = booking.checkInDate;
          const roomType = booking.room?.roomType || 'Room';
          const bookingCode = booking.bookingConfirmationCode;

          const timeSlots = Array.isArray(booking.timeSlots) ? booking.timeSlots : [];

          timeSlots.forEach((slot) => {
            calendarEvents.push({
              title: `${slot.startTime} - ${slot.endTime}\n(${roomType})`,
              date: date,
              allDay: true,
              extendedProps: {
                confirmationCode: bookingCode,
              }
            });
          });
        });

        console.log("üìÖ Events for calendar:", calendarEvents);
        setEvents(calendarEvents);
      } catch (error) {
        console.error("‚ùå Failed to fetch bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>üìÖ Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        eventColor="#0ea5e9"
        height="auto"
        width
        eventDidMount={(info) => {
          const code = info.event.extendedProps.confirmationCode;
          if (code) {
            info.el.title = `Code: ${code}`;
          }
        }}
      />
    </div>
  );
};

export default BookingCalendar;
