import React, { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import '@fullcalendar/daygrid/main.css';
import ApiService from '../../service/ApiService'; // Adjust path as needed

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await ApiService.fetchAllBookings();

        // ✅ Handle custom Response object
        const bookings = response.data.data;

        const calendarEvents = [];

        bookings.forEach((booking) => {
          const date = booking.checkInDate;
          const timeSlots = Array.isArray(booking.timeSlots) ? booking.timeSlots : [];

          timeSlots.forEach((slot) => {
            calendarEvents.push({
              title: slot.label, // Show time slot
              date: date         // Show on correct date
            });
          });
        });

        setEvents(calendarEvents);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        eventColor="#f87171"
        height="auto"
      />
    </div>
  );
};

export default BookingCalendar;
