// BookingCalendar.jsx

import React from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';

const BookingCalendar = ({ roomDetails }) => {
  // 1️⃣ Extract all booked slots from roomDetails
  const bookedEvents = [];

  if (roomDetails && Array.isArray(roomDetails.bookings)) {
    roomDetails.bookings.forEach((booking) => {
      const checkInDate = booking.checkInDate;
      const slots = Array.isArray(booking.timeSlots) ? booking.timeSlots : [];

      slots.forEach((slot) => {
        bookedEvents.push({
          title: `Booked: ${slot.label}`, // Example: "Booked: 10:00 AM - 10:30 AM"
          date: checkInDate,              // Format: "YYYY-MM-DD"
        });
      });
    });
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={bookedEvents}  // ⬅️ Dynamic data here
      />
    </div>
  );
};

export default BookingCalendar;
