import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ApiService from "../../service/ApiService";

const AdminPage = () => {
  const [adminName, setAdminName] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAdminName = async () => {
      try {
        const response = await ApiService.getUserProfile();
        setAdminName(response.user.name);
      } catch (error) {
        console.error("Error fetching admin details:", error.message);
      }
    };

    fetchAdminName();
  }, []);

  return (
    <div className="admin-page">
      <h1 className="welcome-message">Welcome, {adminName} ðŸ‘‹</h1>
      <p className="subtitle">Easily manage your conference bookings and rooms.</p>

      <div className="admin-actions">
        <button className="admin-button" onClick={() => navigate("/admin/manage-rooms")}>
           Manage Rooms
        </button>
        <button className="admin-button" onClick={() => navigate("/admin/manage-bookings")}>
           Manage Bookings
        </button>
        <button className="admin-button" onClick={() => navigate("/home")}>
          â¬… Go Back
        </button>
      </div>
    </div>
  );
};

export default AdminPage;
