// src/component/common/BookingCalendar.jsx

import React from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';

const BookingCalendar = ({ roomDetails }) => {
  // 1️⃣ Prepare events array
  const bookedEvents = [];

  if (roomDetails && Array.isArray(roomDetails.bookings)) {
    roomDetails.bookings.forEach((booking) => {
      const date = booking.checkInDate;
      const timeSlots = Array.isArray(booking.timeSlots) ? booking.timeSlots : [];

      timeSlots.forEach((slot) => {
        bookedEvents.push({
          title: slot.label,  // ➕ Show time slot like "10:00 AM - 10:30 AM"
          date: date          // 📅 Show under the correct date
        });
      });
    });
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={bookedEvents}
        eventColor="#ff6666"
        height="auto"
      />
    </div>
  );
};

export default BookingCalendar;
