import React, { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import '@fullcalendar/daygrid/main.css';
import ApiService from '../../service/ApiService'; // adjust path as needed

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await ApiService.getAllBookings();
        const bookings = response.data; // data contains list of bookings

        const calendarEvents = [];

        bookings.forEach((booking) => {
          const date = booking.checkInDate;
          const timeSlots = Array.isArray(booking.timeSlots) ? booking.timeSlots : [];

          timeSlots.forEach((slot) => {
            const title = slot.startTime && slot.endTime
              ? `${slot.startTime} - ${slot.endTime}`
              : slot.label || 'Booked';

            calendarEvents.push({
              title: title,
              date: date,
            });
          });
        });

        setEvents(calendarEvents);
      } catch (error) {
        console.error("❌ Error fetching bookings for calendar:", error);
      }
    };

    fetchBookings();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        eventColor="#3b82f6" // blue shade
        height="auto"
      />
    </div>
  );
};

export default BookingCalendar;
