import React, { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import '@fullcalendar/daygrid/main.css';
import ApiService from '../../service/ApiService'; // adjust if path differs

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await ApiService.getAllBookings();
        const bookings = response.data;

        const calendarEvents = [];

        bookings.forEach((booking) => {
          const date = booking.checkInDate;
          const roomType = booking.room?.roomType || 'Room';

          const timeSlots = Array.isArray(booking.timeSlots) ? booking.timeSlots : [];

          timeSlots.forEach((slot) => {
            const title = `${slot.startTime} - ${slot.endTime} (${roomType})`;

            calendarEvents.push({
              title,
              date,
              allDay: true
            });
          });
        });

        setEvents(calendarEvents);
      } catch (error) {
        console.error("❌ Error fetching bookings for calendar:", error);
      }
    };

    fetchBookings();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        eventColor="#10b981" // green
        height="auto"
      />
    </div>
  );
};

export default BookingCalendar;
